
//Global Values
const string DisplayName = "Test Display";

//Methods
public void Main(string argument) {

  IMyTextPanel display;
  ValidateDisplay(out display);
  display.WriteText(images[SelectImage()], false);
}

private void ValidateDisplay(out IMyTextPanel displayObject) {

    if(DisplayName == "") throw new Exception("\nName of Display not initialized.\nPlease initailize the name of the LCD within the script.");

    displayObject = (IMyTextPanel) GridTerminalSystem.GetBlockWithName(DisplayName);
    if(displayObject == null) throw new Exception("\nDisplay not found.\nPlease check the displayName again.");
}

private int SelectImage() {
  Random rnd = new Random();
  int result = rnd.Next(0,images.Length);
  Echo($"Value {result} selected.");
  return result;
}

